import "./App.css";
import React, { useEffect, useState } from "react";

//define the mock data with keys
const courses = [
  {
    id: 0,
    name: 'Full Stack Developement Program',
    price: '₹89,999',
    category: 'Software Development',
  },
  {
    id: 1,
    name: 'Python Automation Testing Program',
    price: '₹64,999',
    category: 'Testing',
  },
  {
    id: 2,
    name: 'UI/UX Program',
    price: '₹89,999',
    category: 'Design'
  }
]


function App() {

  const [data, setData] = useState([])

  const fetchData = () => {
      return fetch('https://jsonplaceholder.typicode.com/users')
        .then((res) => res.json())
        .then((d) => setData(d))
  }

  useEffect(() => {
    fetchData();
  }, [])


  /* Mapping the courses into a new array of JSX nodes as arrayDataItems */
  const fetchedData = data.map((dataObj, idx) => {
    return (
      <div>
        <h1>{dataObj.name}</h1>
      </div>
    )
  }
  )

  return (
    <div className="container">
      <div>
        <h1>Filtering List/Array of Items</h1>
      </div>

      {/* returning arrayDataItems wrapped in <ul> */}
      <ul>{fetchData}</ul>
    </div>
  );
}

export default App;
