import "./App.css";
import React, { useEffect, useState } from "react";

function App() {

  //Fetching and Displaying Data
  const [data, setData] = useState([]);

  const fetchData = () => {
    return fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((d) => setData(d));
  };

  useEffect(() => {
    fetchData();
  }, []);


  //Search for a specific data item
  const [query, setQuery] = useState("");

  const search_params = Object.keys(Object.assign({}, ...data));

  function search(data) {
    return data.filter((data) =>
      search_params.some((param) =>
        data[param].toString().toLowerCase().includes(query)
      )
    );
  }

  return (
    <div className="container">
      <center>
        <h1>Searching and Filtering in ReactJS</h1>
      </center>
      <div className="input-box">
        <input
          type="search"
          name="search-form"
          id="search-form"
          className="search-input"
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search user"
        />
      </div>

      <center>
        {search(data).map((dataObj) => {
          return (
            <div className="box">
              <div class="card">
                <div class="category">@{dataObj.username} </div>
                <div class="heading">
                  {dataObj.name}
                  <div class="author">{dataObj.email}</div>
                </div>
              </div>
            </div>
          );
        })}
      </center>
    </div>
  );
}

export default App;
