import "./App.css";
import React, { useEffect, useState } from "react";

//define the mock data with keys
const courses = [
  {
    id: 0,
    name: "Full Stack Developement Program",
    price: "₹89,999",
    category: "Software Development",
  },
  {
    id: 1,
    name: "Python Automation Testing Program",
    price: "₹64,999",
    category: "Testing",
  },
  {
    id: 2,
    name: "UI/UX Program",
    price: "₹89,999",
    category: "Design",
  },
];

function App() {
  const [data, setData] = useState([]);

  const fetchData = () => {
    return fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((d) => setData(d));
  };

  useEffect(() => {
    fetchData();
  }, []);

  const [query, setQuery] = useState("");

  const search_parameters = Object.keys(Object.assign({}, ...data));

 function search(data) {
    return data.filter(
      (data) =>
        search_parameters.some((parameter) =>
        data[parameter].toString().toLowerCase().includes(query)
        )
    );
  }

  return (
    <div className="container">
      <center>
        <h1>Searching and Filtering in ReactJS</h1>
      </center>
      <input type="search"    name="search-form"
              id="search-form"
              className="search-input" onChange={(e) => setQuery(e.target.value)} placeholder="Search user" />
      <center>
        {search(data).map((dataObj) => {
          return (
            <div className="box">
              <div class="card">
                <div class="category">@{dataObj.username} </div>
                <div class="heading">
                  {dataObj.name}
                <div class="author">
                  {dataObj.email}
                </div>
                </div>
              </div>
            </div>
          );
        })}
      </center>
    </div>
  );
}

export default App;
