import "./App.css";
import React, { useEffect, useState } from "react";

function App() {
  const [data, setData] = useState([]);

  const fetchData = () => {
    return fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((d) => setData(d));
  };

  useEffect(() => {
    fetchData();
  }, []);

  const [query, setQuery] = useState("");

  const search_parameters = Object.keys(Object.assign({}, ...data));

  function search(data) {
    return data.filter((data) =>
      search_parameters.some((parameter) =>
        data[parameter].toString().toLowerCase().includes(query)
      )
    );
  }

  const filter_items = [...new Set(data.map((item) => item.region))];

  const [filter, setFilter] = useState("");

  function filteredItems(data) {
    return data.filter(
      (data) =>
        data.region.includes(filter) &&
        search_parameters.some((parameter) =>
          data[parameter].toString().toLowerCase().includes(query)
        )
    );
  }

  return (
    <div className="container">
      <center>
        <h1>Searching and Filtering in ReactJS</h1>
      </center>
      <div className="input-box">
        <input
          type="search"
          name="search-form"
          id="search-form"
          className="search-input"
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search user"
        />
        <div className="select">
 <select
  onChange={(e) => setFilter(e.target.value)}
  className="custom-select"
  aria-label="Filter Countries By Region">
  <option value="">Filter By Region</option>
  {filter_items.map((data) => (
   <option value={data}>Filter By {data}</option>
  ))}
</select>
<span className="focus"></span>
</div>
      </div>
      <center>
        {search(data).map((dataObj) => {
          return (
            <div className="box">
              <div class="card">
                <div class="category">@{dataObj.username} </div>
                <div class="heading">
                  {dataObj.name}
                  <div class="author">{dataObj.email}</div>
                </div>
              </div>
            </div>
          );
        })}
      </center>
    </div>
  );
}

export default App;
